import React, { useState, useContext } from "react";
import axios from "axios";
import GeneralContext from "./GeneralContext"; // ✅ Ensure correct import

const BuyActionWindow = ({ uid }) => {
  const { closeBuyWindow } = useContext(GeneralContext); // ✅ Use useContext correctly
  const [stockQuantity, setStockQuantity] = useState(1);
  const [stockPrice, setStockPrice] = useState(0.0);

  const handleBuyClick = async () => {
    try {
      await axios.post("https://zerodha-clone-backend-z8u8.onrender.com/newOrder", {
        name: uid,
        qty: stockQuantity,
        price: stockPrice,
        mode: "BUY",
      });

      closeBuyWindow(); // ✅ Close buy window only after successful API call
    } catch (error) {
      console.error("Error placing order:", error);
    }
  };

  return (
    <div className="container" id="buy-window">
      <div className="regular-order">
        <div className="inputs">
          <fieldset>
            <legend>Qty.</legend>
            <input
              type="number"
              value={stockQuantity}
              onChange={(e) => setStockQuantity(Number(e.target.value))}
            />
          </fieldset>
          <fieldset>
            <legend>Price</legend>
            <input
              type="number"
              value={stockPrice}
              onChange={(e) => setStockPrice(Number(e.target.value))}
            />
          </fieldset>
        </div>
      </div>
      <div className="buttons">
        <button className="btn btn-blue" onClick={handleBuyClick}>Buy</button>
        <button className="btn btn-grey" onClick={closeBuyWindow}>Cancel</button>
      </div>
    </div>
  );
};

export default BuyActionWindow;
